<template>  <section>    <div class="container" ref="container">      <div class="user singinBx">        <div class="imgBx"></div>        <div class="formBx">          <form action="/" onsubmit="return false">            <h2>Sign In</h2>            <input type="text" v-model="login.username" name="username" placeholder="Username">            <input type="password" v-model="login.password" name="password" placeholder="Password">            <div class="error" v-text="LoginError"></div>            <input type="submit" value="Login" @click="Login">            <p class="signup">Don't have an account ?<a href="javascript:void(0)" @click="toRegister">Register.</a></p>          </form>        </div>      </div>      <div class="user singupBx">        <!--                <div class="formBx">-->        <!--                    <form action="/" onsubmit="return false">-->        <!--                        <h2>Sign In</h2>-->        <!--                        <input type="text" v-model="loginBySms.phone" name="phone" placeholder="Phone">-->        <!--                        <div class="code">-->        <!--                            <input type="text" v-model="code" name="code" placeholder="Code">-->        <!--                            <input type="button" value="Send Sms" @click="sendMsg">-->        <!--                        </div>-->        <!--                        <div class="error" v-text="loginBySmsError"></div>-->        <!--                        <input type="submit" value="Sign in" @click="LoginBySmsFun">-->        <!--                        <p class="signup"><a href="javascript:void(0)" @click="toggleForm">Sign-->        <!--                            In By Password.</a></p>-->        <!--                        <p class="signup">Don't have an account ?<a href="javascript:void(0)" @click="toRegister">Sign-->        <!--                            Up.</a></p>-->        <!--                    </form>-->        <!--                </div>-->        <!--                <div class="imgBx"></div>-->      </div>    </div>  </section></template><script>import request from '@/utils/request'import {reactive, ref} from 'vue'import {useRoute, useRouter} from 'vue-router'import {LoginURL} from '@/utils/Constant'export default {  name: 'Login',  setup() {    let router = useRouter()    let route = useRoute()    let login = reactive({      username: '',      password: '',    })    let LoginError = ref('')    function Login() {      LoginError.value = ''      request.post(LoginURL, login).then((res) => {        if (res.code === 1) {          LoginError.value = res.msg        } else {          localStorage.setItem('user', JSON.stringify(res.data.user))          localStorage.setItem('token', res.data.token)          if (route.query.to !== undefined) {            router.push(route.query.to)          } else {            router.push('/')          }        }      })    }    function toRegister() {      router.push('/register')    }    return {      login,      LoginError,      Login,      toRegister    }  },  components: {},}</script><style scoped>.hidden {  display: none;}* {  box-sizing: border-box;  font-family: "Poppins", sans-serif;}.code {  display: flex;}section {  min-height: 100vh;  background: #d9d9d9;  display: flex;  justify-content: center;  align-items: center;  padding: 20px;}section .container {  position: relative;  width: 800px;  height: 550px;  background: #fff;  box-shadow: 0 15px 50px rgba(0, 0, 0, 0.1);  overflow: hidden;}section .container .user {  position: absolute;  top: 0;  left: 0;  width: 100%;  height: 100%;  display: flex;}section .container .user .imgBx {  position: relative;  width: 50%;  height: 100%;  background: #97dbdc;  transition: .5s;}section .container .user .imgBx img {  position: absolute;  top: 0;  left: 0;  width: 100%;  height: 100%;  object-left: cover;}section .container .user .formBx {  position: relative;  width: 50%;  height: 100%;  background: #fff;  display: flex;  justify-content: center;  align-items: center;  padding: 40px;  transition: .5s;}section .container .user .formBx form h2 {  font-size: 18px;  font-weight: 600;  text-transform: uppercase;  letter-spacing: 2px;  text-align: center;  width: 100%;  margin-bottom: 10px;  color: #555;}section .container .user .formBx form input {  position: relative;  width: 100%;  padding: 10px;  background: #f5f5f5;  color: #333;  border: none;  outline: none;  box-shadow: none;  margin: 8px 0;  font-size: 14px;  letter-spacing: 1px;}section .container .user .formBx form input[type="submit"] {  max-width: 100px;  background: #677eff;  color: #fff;  cursor: pointer;  font-size: 14px;  font-weight: 500;  letter-spacing: 1px;  transition: .5s;}section .container .user .formBx form input[type="button"] {  max-width: 100px;  background: #677eff;  color: #fff;  cursor: pointer;  font-size: 14px;  font-weight: 500;  letter-spacing: 1px;  transition: .5s;}section .container .user .formBx form .signup {  position: relative;  margin-top: 20px;  font-size: 12px;  letter-spacing: 1px;  color: #555;  text-transform: uppercase;  font-weight: 300;}section .container .user .formBx form .signup a {  font-weight: 600;  text-decoration: none;  color: #67eff;}section .container .singupBx {  pointer-events: none;}section .container.active .singupBx {  pointer-events: initial;}section .container .singupBx .formBx {  left: 100%;}section .container.active .singupBx .formBx {  left: 0;}section .container .singupBx .imgBx {  left: -100%;}section .container.active .singupBx .imgBx {  left: 0;}section .container .singinBx .formBx {  left: 0;}section .container.active .singinBx .formBx {  left: 100%;}section .container .singinBx .imgBx {  left: 0;}section .container.active .singinBx .imgBx {  left: 100%;}@media (max-width: 991px) {  section .container {    max-width: 400px;  }  section .container .imgBx {    display: none;  }  section .container .user .formBx {    width: none;  }}.error {  color: red;  font-size: 12px;}</style>